mapper.select:
select
  user_id, username, password, gender, dept_id, is_delete, is_locked, email, telephone, password_change_time, province, city, country, address, creator, create_time, update_user, update_time, login_fail_count, last_login_time
from emis_users


        and user_id = #{userId}


        and username = #{username}


        and password = #{password}


        and gender = #{gender}


        and dept_id = #{deptId}


        and is_delete = #{isDelete}


        and is_locked = #{isLocked}


        and email = #{email}


        and telephone = #{telephone}


        and password_change_time = #{passwordChangeTime}


        and province = #{province}


        and city = #{city}


        and country = #{country}


        and address = #{address}


        and creator = #{creator}


        and create_time = #{createTime}


        and update_user = #{updateUser}


        and update_time = #{updateTime}


        and login_fail_count = #{loginFailCount}


        and last_login_time = #{lastLoginTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from emis_users


        and user_id = #{userId}


        and username = #{username}


        and password = #{password}


        and gender = #{gender}


        and dept_id = #{deptId}


        and is_delete = #{isDelete}


        and is_locked = #{isLocked}


        and email = #{email}


        and telephone = #{telephone}


        and password_change_time = #{passwordChangeTime}


        and province = #{province}


        and city = #{city}


        and country = #{country}


        and address = #{address}


        and creator = #{creator}


        and create_time = #{createTime}


        and update_user = #{updateUser}


        and update_time = #{updateTime}


        and login_fail_count = #{loginFailCount}


        and last_login_time = #{lastLoginTime}

mapper.update:
update emis_users
set is_delete = 1,
    update_user = #{updateUser},
    update_time = #{updateTime}
where user_id = #{userId}
